package com.example.e_ngkringan.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.example.e_ngkringan.databinding.ItemRestaurantBindingimport com.example.e_ngkringan.model.AngkringanDetailclass AngkringanAdapter(    val listANgkringan: List<AngkringanDetail>,    val onItemCallback: OnItemCallBack) : RecyclerView.Adapter<AngkringanAdapter.AngkringanViewHolder>() {    class AngkringanViewHolder(val binding: ItemRestaurantBinding) :        RecyclerView.ViewHolder(binding.root)    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int    ): AngkringanAdapter.AngkringanViewHolder {        return AngkringanViewHolder(            ItemRestaurantBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: AngkringanAdapter.AngkringanViewHolder, position: Int) {        holder.binding.apply {            tvNameAngkringan.text = listANgkringan[position].name            tvAddress.text = listANgkringan[position].address            tvJumlahKursi.text = listANgkringan[position].total_table.toString()            Glide.with(holder.itemView.context)                .load(listANgkringan[position].image)                .into(imageView)            holder.itemView.setOnClickListener {                onItemCallback.onItemClicked(listANgkringan[position])            }        }    }    override fun getItemCount(): Int = listANgkringan.size    interface OnItemCallBack {        fun onItemClicked(data: AngkringanDetail)    }}