package com.example.e_ngkringan.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.example.e_ngkringan.databinding.ItemMenuBindingimport com.example.e_ngkringan.model.DetailMenuclass MenuAdapter(val listMenu: List<DetailMenu>) :    RecyclerView.Adapter<MenuAdapter.MenuViewHolder>() {    class MenuViewHolder(val binding: ItemMenuBinding) : RecyclerView.ViewHolder(binding.root)    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MenuAdapter.MenuViewHolder {        return MenuViewHolder(            ItemMenuBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: MenuAdapter.MenuViewHolder, position: Int) {        holder.binding.apply {            tvNameMenu.text = listMenu[position].name            tvPrice.text = listMenu[position].price.toString()            Glide.with(holder.itemView)                .load(listMenu[position].image)                .into(ivMenu)        }    }    override fun getItemCount(): Int = listMenu.size}