package com.example.e_ngkringan.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.e_ngkringan.databinding.ItemBookingBindingimport com.example.e_ngkringan.model.ListBookingclass RiwayatAdapter(val listRiwayat: List<ListBooking>, val onCallbackClicked: OnCallbackClicked) :    RecyclerView.Adapter<RiwayatAdapter.RiwayatViewHolder>() {    class RiwayatViewHolder(val binding: ItemBookingBinding) : RecyclerView.ViewHolder(binding.root)    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int    ): RiwayatAdapter.RiwayatViewHolder {        return RiwayatViewHolder(            ItemBookingBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: RiwayatAdapter.RiwayatViewHolder, position: Int) {        holder.binding.apply {            idBooking.text = listRiwayat[position].id.toString()            holder.itemView.setOnClickListener {                onCallbackClicked.onClicked(listRiwayat[position])            }        }    }    override fun getItemCount(): Int = listRiwayat.size    interface OnCallbackClicked {        fun onClicked(data: ListBooking)    }}