package com.example.e_ngkringan.apiimport com.example.e_ngkringan.model.*import retrofit2.Callimport retrofit2.http.*interface ApiService {    @GET("/api/v1/resto")    fun getAllAngkringan(): Call<AngkringanResponse>    @GET("/api/v1/resto/{id}")    fun getDetailAngkringan(@Path("id") id: Int): Call<ResponseDetail>    @GET("/api/v1/resto/menu/{code}")    fun getMenu(@Path("code") code: String): Call<ResponseMenu>    @GET("/api/v1/resto/search/{name}")    fun searchAngkringan(@Path("name") name: String): Call<SearchResponse>    @GET("/api/v1/menu/search/{name}")    fun searchMenu(@Path("name") name: String): Call<SearchMenu>    @POST("/api/v1/user/login")    fun login(@Body userLogin: UsersPerson): Call<UsersLogin>    @POST("/api/v1/booking")    fun booking(        @Header("Authorization") authHeader: String,        @Body booking: DetailBooking    ): Call<BookingResponse>    @FormUrlEncoded    @POST("/api/v1/user/register")    fun register(        @Field("name") name: String,        @Field("email") email: String,        @Field("password") password: String    ): Call<UserRegister>    @GET("api/v1/user/current")    fun getUserLogin(@Header("Authorization") authHeader: String): Call<ResponseUserCurrent>    @GET("api/v1/booking/user")    fun getRiwayat(@Header("Authorization") authHeader: String): Call<ResponseListBooking>    @GET("api/v1/booking/{id}")    fun getRiwayatDetail(@Path("id") id: Int): Call<DetailBookingResponse>}