package com.example.e_ngkringan.bookingimport android.util.Logimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport com.example.e_ngkringan.api.ApiConfigimport com.example.e_ngkringan.model.BookingResponseimport com.example.e_ngkringan.model.DetailBookingimport retrofit2.Callimport retrofit2.Callbackimport retrofit2.Responseclass BookingViewModel : ViewModel() {    private val _isLoading = MutableLiveData<Boolean>()    val isLoading: LiveData<Boolean> = _isLoading    fun booking(token: String, dates: String, resto_code: String, note: String) {        _isLoading.value = true        ApiConfig.getApiService().booking(token, DetailBooking(dates, resto_code, note))            .enqueue(object : Callback<BookingResponse> {                override fun onResponse(                    call: Call<BookingResponse>,                    response: Response<BookingResponse>                ) {                    _isLoading.value = false                    if (response.isSuccessful) {                        Log.d("wesi", "${response.body()}")                    }                }                override fun onFailure(call: Call<BookingResponse>, t: Throwable) {                    _isLoading.value = false                }            })    }}