package com.example.e_ngkringan.bookingimport androidx.datastore.preferences.protobuf.Apiimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport com.example.e_ngkringan.api.ApiConfigimport com.example.e_ngkringan.model.DetailBookingimport com.example.e_ngkringan.model.DetailBookingResponseimport com.example.e_ngkringan.model.ListBookingsimport retrofit2.Callimport retrofit2.Callbackimport retrofit2.Responseclass RiwayatDetailViewModel : ViewModel() {    private val _detailBooking = MutableLiveData<ListBookings>()    val detailBooking: LiveData<ListBookings> = _detailBooking    private val _isLoading = MutableLiveData<Boolean>()    val isLoading: LiveData<Boolean> = _isLoading    fun getDetail(id: Int) {        _isLoading.value = true        ApiConfig.getApiService().getRiwayatDetail(id)            .enqueue(object : Callback<DetailBookingResponse> {                override fun onResponse(                    call: Call<DetailBookingResponse>,                    response: Response<DetailBookingResponse>                ) {                    _isLoading.value = false                    if (response.isSuccessful) {                        _detailBooking.postValue(response.body()?.data)                    }                }                override fun onFailure(call: Call<DetailBookingResponse>, t: Throwable) {                    _isLoading.value = false                }            })    }}