package com.example.e_ngkringan.bookingimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport com.example.e_ngkringan.api.ApiConfigimport com.example.e_ngkringan.model.ListBookingimport com.example.e_ngkringan.model.ResponseListBookingimport retrofit2.Callimport retrofit2.Callbackimport retrofit2.Responseclass RiwayatViewHolder : ViewModel() {    private val _listRiwayat = MutableLiveData<List<ListBooking>>()    val listBooking: LiveData<List<ListBooking>> = _listRiwayat    private val _list = MutableLiveData<ListBooking>()    val list: LiveData<ListBooking> = _list    private val _isLoading = MutableLiveData<Boolean>()    val isLoading: LiveData<Boolean> = _isLoading    fun getRiwayat(token: String) {        _isLoading.value = true        ApiConfig.getApiService().getRiwayat(token)            .enqueue(object : Callback<ResponseListBooking> {                override fun onResponse(                    call: Call<ResponseListBooking>,                    response: Response<ResponseListBooking>                ) {                    _isLoading.value = false                    if (response.isSuccessful) {                        _listRiwayat.postValue(response.body()?.data)                    }                }                override fun onFailure(call: Call<ResponseListBooking>, t: Throwable) {                    _isLoading.value = false                }            })    }}