package com.example.e_ngkringan.detailimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport com.example.e_ngkringan.api.ApiConfigimport com.example.e_ngkringan.model.AngkringanDetailimport com.example.e_ngkringan.model.DetailMenuimport com.example.e_ngkringan.model.ResponseDetailimport com.example.e_ngkringan.model.Restoimport retrofit2.Callimport retrofit2.Callbackimport retrofit2.Responseclass DetailViewModel : ViewModel() {    private val _angkringanDetail = MutableLiveData<AngkringanDetail>()    val angkringanDetail: LiveData<AngkringanDetail> = _angkringanDetail    private val _isLoading = MutableLiveData<Boolean>()    val isLoading: LiveData<Boolean> = _isLoading    fun getDetailAngkringan(id: Int) {        _isLoading.value = true        ApiConfig.getApiService().getDetailAngkringan(id)            .enqueue(object : Callback<ResponseDetail> {                override fun onResponse(                    call: Call<ResponseDetail>,                    response: Response<ResponseDetail>                ) {                    _isLoading.value = false                    if (response.isSuccessful) {                        _angkringanDetail.postValue(response.body()?.data)                    }                }                override fun onFailure(call: Call<ResponseDetail>, t: Throwable) {                    _isLoading.value = false                }            })    }}