package com.example.e_ngkringan.menuimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport com.example.e_ngkringan.api.ApiConfigimport com.example.e_ngkringan.model.*import retrofit2.Callimport retrofit2.Callbackimport retrofit2.Responseclass MenuViewModel : ViewModel() {    private val _listMenu = MutableLiveData<List<DetailMenu>>()    val listMenu: LiveData<List<DetailMenu>> = _listMenu    private val _isLoading = MutableLiveData<Boolean>()    val isLoading: LiveData<Boolean> = _isLoading    fun getMenu(code: String) {        _isLoading.value = true        ApiConfig.getApiService().getMenu(code)            .enqueue(object : Callback<ResponseMenu> {                override fun onResponse(                    call: Call<ResponseMenu>,                    response: Response<ResponseMenu>                ) {                    _isLoading.value = false                    if (response.isSuccessful) {                        _listMenu.postValue(response.body()?.data)                    }                }                override fun onFailure(call: Call<ResponseMenu>, t: Throwable) {                    _isLoading.value = false                }            })    }    fun searchMenu(name: String) {        _isLoading.value = true        ApiConfig.getApiService().searchMenu(name)            .enqueue(object : Callback<SearchMenu> {                override fun onResponse(call: Call<SearchMenu>, response: Response<SearchMenu>) {                    _isLoading.value = false                    if (response.isSuccessful) {                        _listMenu.postValue(response.body()?.data)                    }                }                override fun onFailure(call: Call<SearchMenu>, t: Throwable) {                    _isLoading.value = true                }            })    }}