package com.example.e_ngkringan.modelimport androidx.lifecycle.MutableLiveDatadata class UsersPerson(    val email: String,    val password: String)data class UsersLogin(    val id: String,    val code: String,    val name: String,    val email: String,    val token: String)data class UserRegister(    val status: String,    val data: User)data class User(    val user: MutableList<ResponseUserCurrent>)data class ResponseUserCurrent(    val id: Int,    val code: String,    val name: String,    val email: String,    val password: String,    val createdAt: String,    val updatedAt: String)