package com.example.e_ngkringan.utilimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport com.example.e_ngkringan.data_store.UserLoginPreferencesimport com.example.e_ngkringan.login.LoginViewModelclass PreferenceFactory(private val userLoginPreferences: UserLoginPreferences) :    ViewModelProvider.NewInstanceFactory() {    override fun <T : ViewModel> create(modelClass: Class<T>): T {        if (modelClass.isAssignableFrom(LoginViewModel::class.java)) {            return LoginViewModel(userLoginPreferences) as T        }        throw IllegalArgumentException("Undefined class ${modelClass.name}")    }}